{
  "index.html": "<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Simple BODMAS Calculator</title>
    <link rel="stylesheet" href="styles.css">
</head>
<body>
    <div class="calculator-container">
        <h1>BODMAS Calculator</h1>
        <div class="input-group">
            <input type="number" id="num1" placeholder="Enter first number">
            <input type="number" id="num2" placeholder="Enter second number">
        </div>
        <div class="operations">
            <button data-operator="+">+</button>
            <button data-operator="-">-</button>
            <button data-operator="*">*</button>
            <button data-operator="/">/</button>
        </div>
        <div class="result-group">
            <p>Result: <span id="result"></span></p>
        </div>
    </div>
    <script src="app.js"></script>
</body>
</html>
",
  "styles.css": "body {
    font-family: Arial, sans-serif;
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 100vh;
    margin: 0;
    background-color: #f4f4f4;
}

.calculator-container {
    background-color: #fff;
    padding: 30px;
    border-radius: 8px;
    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
    text-align: center;
    width: 300px;
}

h1 {
    color: #333;
    margin-bottom: 25px;
}

.input-group input[type="number"] {
    width: calc(100% - 20px);
    padding: 10px;
    margin-bottom: 15px;
    border: 1px solid #ddd;
    border-radius: 4px;
    font-size: 1em;
}

.operations {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 10px;
    margin-bottom: 25px;
}

.operations button {
    padding: 15px;
    font-size: 1.5em;
    border: none;
    border-radius: 4px;
    background-color: #007bff;
    color: white;
    cursor: pointer;
    transition: background-color 0.2s ease;
}

.operations button:hover {
    background-color: #0056b3;
}

.result-group p {
    font-size: 1.2em;
    color: #555;
    margin: 0;
}

#result {
    font-weight: bold;
    color: #28a745;
}
",
  "app.js": "document.addEventListener('DOMContentLoaded', () => {
    const num1Input = document.getElementById('num1');
    const num2Input = document.getElementById('num2');
    const resultSpan = document.getElementById('result');
    const operationButtons = document.querySelectorAll('.operations button');

    operationButtons.forEach(button => {
        button.addEventListener('click', () => {
            const num1 = parseFloat(num1Input.value);
            const num2 = parseFloat(num2Input.value);
            const operator = button.dataset.operator;
            let result;

            if (isNaN(num1) || isNaN(num2)) {
                resultSpan.textContent = 'Please enter valid numbers';
                resultSpan.style.color = 'red';
                return;
            }

            switch (operator) {
                case '+':
                    result = num1 + num2;
                    break;
                case '-':
                    result = num1 - num2;
                    break;
                case '*':

       result = num1 * num2;
                    break;
                case '/':
                    if (num2 === 0) {
                        resultSpan.textContent = 'Division by zero is not allowed';
                        resultSpan.style.color = 'red';
                        return;

} else {
                        result = num1 / num2;
                    }
                    break;
                default:
                    resultSpan.textContent = 'Invalid operation';
                    resultSpan.style.color = 'red';
                    return;
            }
            resultSpan.textContent = result.toFixed(2); // Display result with 2 decimal places
            resultSpan.style.color = '#28a745'; // Reset color to success
        });
    });
});
"
